import { createSlice } from "@reduxjs/toolkit";

export const SelectLogoSlice = createSlice({
  name: "SelectLogoSlice",
  initialState: [
    {
      value: {
        IsOpen: false,
        Logo: "",
        Name: "שן המוצר",
        Details: "",
        PriceMajor: "00",
        PriceMinor: "00",
        Background: "",
        Notes: "",
      },
    },
  ],
  reducers: {
    Open: (state) => {
      state.IsOpen.value = true;
    },
    ChangeName: (state, action) => {
      state.Name.value = action.payload;
    },
    ChangeDetails: (state, action) => {
      state.Details.value = action.payload;
    },
  },
});

export const { Open, ChangeName, ChangeDetails } = SelectLogoSlice.actions;

export default SelectLogoSlice.reducer;
